    FROM t_actividades a
    join m_centros_costos b on (a.id_centro_costo=b.id_centro_costo)
    left join m_subcentros_costos c on (a.id_subcentro_costo=c.id_subcentro_costo)
    WHERE id_actividad = OLD.id_actividad;

