  UPDATE m_centros_costos SET horas = v_total_horas_centro
    WHERE id_centro_costo = v_id_centro_costo;

  UPDATE m_subcentros_costos SET horas = v_total_horas_subcentro
    WHERE id_subcentro_costo = v_id_subcentro_costo;
END */;;
