CREATE PROCEDURE `sp_actualizar_horas`(
in p_id_actividad INT,
in p_horas FLOAT)
BEGIN
/**
@description Actualiza las horas de una actividad y de los centros de costo asociados
@example call sp_actualizar_horas(2, 10)
*/

    DECLARE v_horas_previas FLOAT;
    DECLARE v_id_centro_costo INT;
    DECLARE v_id_subcentro_costo INT;
    DECLARE v_horas_centro FLOAT;
    DECLARE v_horas_subcentro FLOAT;
	  DECLARE v_total_horas_centro FLOAT;
    DECLARE v_total_horas_subcentro FLOAT;

  SELECT
		horas, id_centro_costo, id_subcentro_costo
	INTO
		v_horas_previas, v_id_centro_costo, v_id_subcentro_costo
    FROM t_actividades
    WHERE id_actividad = p_id_actividad;

	SELECT ifnull(horas, 0) INTO v_horas_centro
    FROM m_centros_costos WHERE id_centro_costo = v_id_centro_costo;

  SELECT ifnull(horas, 0) INTO v_horas_subcentro
    FROM m_subcentros_costos WHERE id_subcentro_costo = v_id_subcentro_costo;

	IF v_horas_centro > 0 THEN
	  SET v_total_horas_centro = (v_horas_centro - v_horas_previas) + p_horas;
    SET v_total_horas_centro = IF(v_total_horas_centro<0, 0, v_total_horas_centro);
	ELSE
		SET v_total_horas_centro = p_horas;
	END IF;
  IF v_horas_subcentro > 0 THEN
		SET v_total_horas_subcentro = (v_horas_subcentro - v_horas_previas) + p_horas;
    SET v_total_horas_subcentro = if(v_total_horas_subcentro<0, 0, v_total_horas_subcentro);
	ELSE
    SET v_total_horas_subcentro = p_horas;
  END IF;

  UPDATE m_centros_costos SET horas = v_total_horas_centro
    WHERE id_centro_costo = v_id_centro_costo;

  UPDATE m_subcentros_costos SET horas = v_total_horas_subcentro
    WHERE id_subcentro_costo = v_id_subcentro_costo;


END ;;
DELIMITER ;
